mapper.select:
select user.*,role.*,school.id as school_id,school.name as school_name
from user left outer join school on user.school_id = school.id,role
where user.role_id=role.id and user.id=#{uid}

select user.*,school.id as school_id,school.name as school_name
from user left outer join school on user.school_id = school.id
where username=#{name} and password=#{password}
